df_test = pd.read_csv("data/df_test.csv")

gold_labels = df_test["label"].tolist()


prompt = create_prompt(
    df_train, is_guideline, is_few_shot, is_chain_of_thought, chain_of_thought_v2
)

#add each dev text to the prompt
labels = []
for text in tqdm.tqdm(df_test["text"]):
    prompt_for_instance = prompt + f'\n\n###\n"{text}"\n--> '

    if not is_chain_of_thought:
        max_tokens = 1
    else:
        max_tokens = 256

    response = chat_with_backoff(
        model="gpt-3.5-turbo",
        messages=[{"role": "user", "content": prompt_for_instance}],
        temperature=0.7,
        max_tokens=max_tokens,
        top_p=1,
        frequency_penalty=0,
        presence_penalty=0,
    )

    # get the label from the response
    if is_chain_of_thought:
        label = response["choices"][0]["message"]["content"][-1]
    else:
        label = response["choices"][0]["message"]["content"]

    if label == "1" or label == "0":
        label = int(label)
    else:
        label = -1
    labels.append(label)

    # save label to file
