    tp = 0
    fp = 0
    fn = 0
    for gold, label in zip(gold_labels, labels):
        if gold == 1 and label == 1:
            tp += 1
        elif gold == 1 and label == 0:
            fn += 1
        elif gold == 0 and label == 1:
            fp += 1
    precision = tp / (tp + fp)
    recall = tp / (tp + fn)
    f1 = 2 * (precision * recall) / (precision + recall)
    print(f"F1-Score: {f1}")

    exit()


